        [Switch]
        $MachineType,

        [Switch]
        $AutoEnrollment,

        [Switch]
        $NoSecurityExtension,

        [Switch]
        $EnrolleeSuppliesSubject,

        [Management.Automation.PSCredential]
        [Management.Automation.CredentialAttribute()]
        $Credential = [Management.Automation.PSCredential]::Empty,

        [Switch]
        $Raw,

        [Switch]
        $SSL,

        [Switch]
        $Obfuscate,

        [String]
        $Certificate,

        [String]
        $CertPassword
    )

